import React, { Component, Fragment } from 'react';
import './Search.css';
import SearchResultItem from './SearchResultItem';
import SearchResultNone from './SearchResultNone';
import SearchResultNum from './SearchResultNum';
import Data from './SearchData.json';

import API from '../../api/api';
import axios from 'axios';

class Search extends Component{

	constructor (props)
	{
		super(props);
		this.state = {
			data: [],
			count: 0
		}
		this.renderSearchResult = this.renderSearchResult.bind(this);
	}

	async getSearchResult(){
		const responce = await axios.get(
			'https://jsonplaceholder.typicode.com/users'
		);
		// console.log("DEBUG", responce);
		// console.log(responce.data);
		return (responce);
	}

	renderSearchResult(){
		console.log("status",this.state.count);
		if (this.state.count === 0)
		{
			console.log("DEBUG THIS IS DATA NONE");
			return(
				<Fragment>
					<SearchResultNum count = {this.state.count}/>
					<SearchResultNone />
					</Fragment>
			);
		}
		else
		{
			console.log("DEBUG THIS IS DATA YEAH");
			return(
				<Fragment>

				<SearchResultNum count = {this.state.count}/>,
				<SearchResultItem Data = {Data.data}/>
				</Fragment>

			);
		}
	}

	render() {
		let temp;

		return(
			<Fragment>
				<div class="search-container">
					<input type="text" placeholder="도서명, 저자를 입력하세요."/>
					<input type="submit" id="btn" class="test" value="검색" onClick={() => this.getSearchResult().then(
						response => this.setState({data: response, count: response.status}))}/>
				</div>
				{this.renderSearchResult()}
			</Fragment>
		);
	}
}

export default Search;
