import React from 'react';
// import './ItemInfo.css';
import {Link} from 'react-router-dom';

export function Button(props)
{
	let value;
	let className;

	if (props.active === false)
	{
		value = "대출불가";
		className = "no_borrow";
	}
	else if (!props.checkout) // active true & 대여사항이 없다? -> 대여가능
	{
		value = "대출하기";
		className = "borrow";
	}
	else if (props.checkout.overdueDay <= 0){ //active true & 대여사항있다 & 음수나 양수? -> 반납
		value = "반납하기";
		className = "return";
	}
	else{ //사실 위에도 else처리해도 됨
		value = "반납하기";
		className = "return";
	}

	return (
		<form action={value} method="GET">
			<Link to={`/checkout/${props.id}`}>
				<input type="button" class={className} value = {value}/>
			</Link>
		</form>
	);
}

export default Button;
