import React from 'react';
import './ItemInfo.css'

export function Button(props) {
	let value;

	switch (props.bookState)
	{
		case "대출하기":
			value = "대출하기";
			break;
		case "반납하기":
			value = "반납하기";

			break;
		case "대출불가":
			value = "대출불가";
			break;
		default:
			value = "undefined";
			break;
	}

	return (
		<form action={props.bookState} method="GET">
			<input type="button" class={props.bookState} value = {value}/>
		</form>
	);
}
