import React, { Component, Fragment } from 'react';
import './Search.css';
import SearchResultItem from './SearchResultItem';
import SearchResultNone from './SearchResultNone';
import SearchResultNum from './SearchResultNum';
import Data from './SearchData.json';

import API from '../../api/api';
import axios from 'axios';

class Search extends Component{

	constructor (props)
	{
		super(props);
		this.state = {
			data: null
		}
		this.renderSearchResult = this.renderSearchResult.bind(this);
	}

	async getData(){
		const responce = await axios.get(
			'https://jsonplaceholder.typicode.com/users'
		);
		return (responce.data);
	}

	renderSearchResult(){
		let searchResult;

		<SearchResultNum count = {Data.count}/>
		if (Data.count === 0)
			searchResult = <SearchResultNone />
		else
			searchResult = <SearchResultItem Data = {Data.data}/>
	}

	render() {
		let temp;

		return(
			<Fragment>
				<div class="search-container">
					<input type="text" placeholder="도서명, 저자를 입력하세요."/>
					<input type="submit" id="btn" class="test" value="검색" onClick={() => this.getData().then(
						data => this.setState({state: data})
						)}/>
				</div>
				{this.renderSearchResult()}
			</Fragment>
		);
	}
}

export default Search;
