import React, { Component }from 'react';

class Button extends Component{

	render () {
		let value;
		switch (bookState)
		{
			case "대출하기":
				value = "대출하기";
				break;
			case "반납하기":
				value = "반납하기";
				break;
			case "대출불가":
				value = "대출불가";
				break;
			default:
				value = "undefined";
				break;
		}

		return (
			<form action={value} method="GET">
				<input type="button" class={value} value = {value}/>
			</form>
		);
	}
}

export default Button;
