import React from 'react';
// import './ItemInfo.css';

export function Button(props)
{
	let value;
	let className;

	console.log(props.isOverdue);
	if (props.isOverdue)
		console.log(props.isOverdue.overdueDay);

	if (!props.isOverdue) // 연체가 없다?
	{
		value = "대출하기";
		className = "borrow";
	}
	else if (props.isOverdue.overdueDay <= 0){ //연체가 있다?
		value = "반납하기";
		className = "return";
	}
	else{
		value = "연체!!!!!!";
		className = "return";
	}

	return (
		<form action={value} method="GET">
			<input type="button" class={className} value = {value}/>
		</form>
	);
}

export default Button;
