import React, { Component } from 'react';
import './ItemInfo.css';

// 저거 대출하기.. 비교해서 넣는거 해야됨. 
class ButtonControlle extends Component{

	constructor(props)
	{
		super(props);
		this.state = {BookState : "borrow"};
	}

	render () {
		let isCheckout = this.state.BookState;
		let button;

		if (isLoggedIn) {
			button = <LogoutButton onClick={this.handleLogoutClick} />;
		} else {
			button = <LoginButton onClick={this.handleLoginClick} />;
		}
		return (




			<div class="item">
				<img src={this.props.imgUrl} alt="책1"/>
				<div class="item-info">
					<h2>{this.props.bookName}</h2>
					<h3>{this.props.bookAuthor}</h3>
					<form action="#borrow" method="GET">
						<input type="button" class={this.props.bookStatus} />
					</form>
				</div>
			</div>
		);
	}
}

export default ItemInfo;
