import React, { Component }from 'react';
import './ItemInfo.css';

export function Button(props)
{
	let value;
	switch (this.props.bookState)
	{
		case "borrow":
			value = "대출하기";
			break;
		case "return":
			value = "반납하기";
			break;
		case "no-borrow":
			value = "대출불가";
			break;
		default:
			value = "undefined";
			break;
	}

	return (
		<form action={value} method="GET">
			<input type="button" class={value} value = {value}/>
		</form>
	);
}

export default Button;
