import React, { Component, Fragment } from 'react';
import './Checkin.css';
import { CheckinDate } from '../utils/Date.js';
import { IsOverdue } from '../utils/IsCheckout';
import {Link} from 'react-router-dom';
import axios from 'axios';

class Checkin extends Component{

	constructor(props)
	{
		super(props);
		state = {
			selectedOption: true
		}
	}

	async postCheckinInfo(){
		const request = await axios.post('http://localhost:3000/checkin/' + this.state.selectedOption)
	}

	handleOptionChange = (changeEvent) => {
		this.setState({
		  selectedOption: changeEvent.target.value
		});
	};

	render() {
		let CheckinInfo = this.props.location.state.data;
		console.log("CHECKIN", CheckinInfo);
		if (CheckinInfo !== undefined)
		{
			return (
				<Fragment>
					<article>
						<div class="book_title">
							<h2>[{CheckinInfo.book.title}] 반납하기</h2>
						</div>
						<div class="checkin-container">
							<div class="checkin_info">
								<h3>반납자</h3>
								<input type="text" class="info_name" placeholder={CheckinInfo.user.userName}disabled/>
							</div>
							<div class="checkin_info">
								<div class="info_date">
									<h3>반납 일시</h3>
									<IsOverdue overdueDay = {CheckinInfo.overdueDay}/>
								</div>
								<CheckinDate />
							</div>
							<div class="checkin_info">
								<h3>책 상태</h3>
								<input type="text" class="info_state" placeholder={CheckinInfo.checkoutStatus} />
							</div>
							<div class="checkin_info">
								<h3>대출 가능 유무</h3>
								<ul class="checkin_radio">
									<li><input type="radio" value={true} checked/>가능</li>
									<li><input type="radio" value={false}/>불가능</li>
								</ul>
							</div>
						</div>
						<div class="checkin_button">
							<form onSubmit={this.handleFormSubmit}>
								<input type="submit" class="btn" value="반납하기"/>
							</form>
						</div>
					</article>
				</Fragment>
			);
		}
		else
		{
			return (
				<div class="checkin-container">
					<div class="checkin-undefined">
						<h1>404 Not Found</h1>
						<h3>{CheckinInfo.status}</h3>
						<Link to="/">
							<input type="button" class="btn" value="돌아가기"/>
						</Link>
					</div>
				</div>
			);
		}
	}
}

export default Checkin;
