import './ItemInfo.css';
import './Button.js'
import { CheckOutButton, CheckInButton } from './Button.js';

// 저거 대출하기.. 비교해서 넣는거 해야됨. 
class ItemInfo extends Component{
	constructor(props)
	{
		super(props);
		this.state = {BookState : "borrow"};
	}

	render () {
		let button;

		if (this.props.bookState === "borrow") {
			button = <CheckOutButton bookState = {this.props.bookState} />;
		} else if (this.props.bookState === "return"){
			button = <CheckInButton bookState = {this.props.bookState} />;
