		return (
			<div class="item">
				<img src={this.props.imgUrl} alt="책1"/>
				<div class="item-info">
					<h2>{this.props.bookName}</h2>
					<h3>{this.props.bookAuthor}</h3>
					<form action="#borrow" method="GET">
					</form>
				</div>
			</div>
		);
	}
}

export default ItemInfo;
