import React, { Component, Fragment } from 'react';
import './Search.css';
import SearchResultItem from './SearchResultItem';
import SearchResultNone from './SearchResultNone';
import SearchResultNum from './SearchResultNum';
import Data from './SearchData.json';

import API from './api';
import axios from 'axios';

class Search extends Component{
	render() {
		let searchResult;
		if (Data.count === 0)
			searchResult = <SearchResultNone />
		else
			searchResult = <SearchResultItem Data = {Data.data}/>

		return(
			<Fragment>
				<div class="search-container">

					{/* {axios.get(`${API}/book/search?`} */}
					{/* <form actioacn="#search" method="GET"> */}
						<input type="text" placeholder="도서명, 저자를 입력하세요."/>
						{/* get요청부분 */}
						<Button type="submit" id="btn" class="test" value="검색"/>
					{/* </form> */}
				</div>
				<SearchResultNum count = {Data.count}/>
				{searchResult}
			</Fragment>
		);
	}
}

export default Search;
