import React from 'react';
import './ItemInfo.css';

export function Button(props)
{
	let value;
	let className;

	console.log(props.isOverdue);
	if (props.isOverdue)
		console.log(props.isOverdue.overdueDay);
	if (props.isOverdue.overdueDay <= 0|| !props.isOverdue)
	{
		value = "대출하기";
		className = "borrow";
	}
	else if (props.isOverdue.overdueDay > 0)
	{
		value = "반납하기"
		className = "return";
	}

	return (
		<form action={value} method="GET">
			<input type="button" class={className} value = {value}/>
		</form>
	);
}

export default Button;
