import React, { Component, Fragment } from 'react';
import './Checkout.css';
import Data from './Checkout.json';
import { CheckoutDate } from '../utils/date';

class Checkout extends Component{
	render() {
		const CheckoutInfo = Data.data;
		console.log(CheckoutInfo);
		if (CheckoutInfo)
		{
			return (
				<Fragment>
					<article>
						<div class="book_title">
							<h2>[{CheckoutInfo.title}] 대출하기</h2>
						</div>
						<div class="borrow-container">
							<div class="borrow_info">
								<h3>대출자</h3>
								<input type="text" class="info_name" placeholder="대출자 이름"/>
							</div>
							<div class="borrow_info">
								<h3>대출 일시</h3>
								<CheckoutDate />
							</div>
							<div class="borrow_info">
								<h3>책 상태</h3>
								<input type="text" class="info_state" placeholder={CheckoutInfo.status}/>
							</div>
						</div>
						<div class="borrow_button">
							<form action="#borrow" method="GET">
								<input type="button" class="btn" value="대출하기"/>
							</form>
						</div>
					</article>
				</Fragment>
			);
		}
	}
}

export default Checkout;
