import React, { Component, Fragment } from 'react';
import './Checkout.css';
import { CheckoutDate } from '../utils/Date';
import {Link} from 'react-router-dom';
import axios from 'axios';

class Checkout extends Component{

	constructor(props)
	{
		super(props);
		this.state = {
			data: null
		}
	}

	async getBookInfo(){
		let response;
		if (!this.props.checkout) // 대출
		{
			response = await axios.get(
				'http://localhost:3000/book/' + this.props.id
			);
		}
		else // 반납
		{
			response = await axios.get(
				'http://localhost:3000/checkout/' + this.props.checkout.id
			);
		}
		console.log(response);
		return (response);
	}

	componentDidMount() {
		this.getBookInfo().then(
			info => this.setState(
				{
					data:	info.data.data
				}
			)
		)
	}

	render() {
		let CheckoutInfo = this.props.location.state.data;
		console.log("CHECKOUT", this.state.data.status);
		if (CheckoutInfo !== undefined)
		{
			return (
				<Fragment>
					<article>
						<div class="book_title">
							<h2>[{CheckoutInfo.title}] 대출하기</h2>
						</div>
						<div class="checkout-container">
							<div class="checkout_info">
								<h3>대출자</h3>
								<input type="text" class="info_name" placeholder="대출자 이름"/>
							</div>
							<div class="checkout_info">
								<h3>대출 일시</h3>
								<CheckoutDate />
							</div>
							<div class="checkout_info">
								<h3>책 상태</h3>
								<input type="text" class="info_state" placeholder={CheckoutInfo.status}/>
							</div>
						</div>
						<div class="checkout_button">
							<form action="#checkout" method="POST">
								<input type="button" class="btn" value="대출하기"/>
							</form>
						</div>
					</article>
				</Fragment>
			);
		}
		else
		{
			return (
				<div class="checkout-container">
					<div class="checkout-undefined">
						<h1>404 Not Found</h1>
						<h3>{this.state.data.status}</h3>
						<Link to="/">
							<input type="button" class="btn" value="돌아가기"/>
						</Link>
					</div>
				</div>
			);
		}
	}
}

export default Checkout;
