import React, { Component } from 'react';
import './ItemInfo.css';

// 저거 대출하기.. 비교해서 넣는거 해야됨. 
class ItemInfo extends Component{
	render (){
		return (
			<div class="item">
				<img src={this.props.imgUrl} alt="책1"/>
				<div class="item-info">
					<h2>{this.props.bookName}</h2>
					<h3>{this.props.bookAuthor}</h3>
					<form action="#borrow" method="GET">
						<input type="button" class={this.props.bookStatus} value="대출하기"/>
					</form>
				</div>
			</div>
		);
	}
}

export default ItemInfo;
