import React, { Component, Fragment } from 'react';
import './Search.css';
import SearchResultItem from './SearchResultItem';
import SearchResultNone from './SearchResultNone';
import SearchResultNum from './SearchResultNum';
import Data from './SearchData.json';

import API from '../../api/api';
import axios from 'axios';

class Search extends Component{

	constructor(props){
		super(props);
		this.getData = this.getData.bind(this);
	}

	async getData(){
		const res = await axios.get(
			'https://jsonplaceholder.typicode.com/users'
		);
		console.log(res);
		console.log(res.data);
		return (res.data);
	}

	render() {
		let searchResult;
		if (Data.count === 0)
			searchResult = <SearchResultNone />
		else
			searchResult = <SearchResultItem Data = {Data.data}/>

		return(
			<Fragment>
				<div class="search-container">
					{/* {axios.get(`${API}/book/search?`} */}
					{/* <form actioacn="#search" method="GET"> */}
						<input type="text" placeholder="도서명, 저자를 입력하세요."/>
						{/* get요청부분 */}
						<input type="submit" id="btn" class="test" value="검색" onClick={getData()}/>
					{/* </form> */}
				</div>
				<SearchResultNum count = {Data.count}/>
				{searchResult}
			</Fragment>
		);
	}
}

export default Search;
