import React, { Component, Fragment } from 'react';
import './Search.css';
import SearchResultItem from './SearchResultItem';
import SearchResultNone from './SearchResultNone';
import SearchResultNum from './SearchResultNum';
import Data from './SearchData.json';

import API from '../../api/api';
import axios from 'axios';

class Search extends Component{

	constructor (props)
	{
		super(props);
		this.state = {
			data: [],
			count: 0,
			text: ''
		}
		this.renderSearchResult = this.renderSearchResult.bind(this);
		// this.handleSearchTextChange = this.handleSearchTextChange.bind(this);
	}

	async getSearchResult(){
		const response = await axios.get(
			'http://localhost:3000/book/search?query=' + this.state.text
		);
		return (response);
	}

	renderSearchResult(){
		console.log("##############");
		if (this.state.count === 0)
		{
			return(
				<Fragment>
					<SearchResultNum count = {this.state.count}/>
					<SearchResultNone />
				</Fragment>
			);
		}
		else
		{
			return(
				<Fragment>
					<SearchResultNum count = {this.state.count}/>,
					<SearchResultItem Data = {Data.data}/>
				</Fragment>
			);
		}
	}

	handleSubmit = (e) => {
		this.getSearchResult().then(
			temp => this.setState(
				{
					data:	temp.data,
					count:	temp.status
				}
			)
		)
		this.renderSearchResult();
	}

	handleSearchTextChange = (e) => {
		this.setState({
			text: e.target.value
		})
	}

	handleOnClick = (e) => {
		e.preventDefault();

	}

	render() {
		let temp;

		return(
			<Fragment>
				<div class="search-container">
					<form onSubmit={this.handleSubmit}>
						<input type="text" placeholder="도서명, 저자를 입력하세요." onChange={this.handleSearchTextChange}/>
						<input type="submit" id="btn" class="test" value="검색" />
					</form>
				</div>
			</Fragment>
		);
	}
}

export default Search;
