import React, { Component, Fragment } from 'react';
import './Checkout.css';
import { CheckoutDate } from '../utils/Date';
import {Link} from 'react-router-dom';
import axios from 'axios';

class Checkout extends Component{

	constructor(props)
	{
		super(props);
		this.state = {
			isSuccess: false,
			userName: '',
			checkoutStatus: this.props.location.state.data.status
		}
	}

	async postCheckoutInfo(){
		let request;
		request = await axios.post('http://localhost:3000/checkout/' + this.props.location.state.data.id, 
		{
			"userName" : this.state.userName,
			"checkoutStatus" : this.state.checkoutStatus
		}).then(function (response) {
			console.log(response.status);
			return(response.status);
		}).catch(function (error) {
			console.log(error.response.status);
			return(error.response.status);
		});
		return(request);
	}

	handleFormSubmit = async (formSubmitEvent) => {
		formSubmitEvent.preventDefault();
		let response = await this.postCheckoutInfo();
		console.log(response);
		if (response === 200)
			this.state.isSuccess = true;
	};

	handleNameChange = (changeEvent) => {
		changeEvent.preventDefault();
		this.setState({
			userName: changeEvent.target.value
		});
	};

	handleStatusChange = (changeEvent) => {
		changeEvent.preventDefault();
		this.setState({
			checkoutStatus: changeEvent.target.value
		});
	};

	render() {
		let CheckoutInfo = this.props.location.state.data;
		if (CheckoutInfo !== undefined)
		{
			return (
				<Fragment>
					<article>
						<div class="book_title">
							<h2>[{CheckoutInfo.title}] 대출하기</h2>
						</div>
						<div class="checkout-container">
							<div class="checkout_info">
								<h3>대출자</h3>
								<input type="text" class="info_name" placeholder="대출자 이름" onChange={this.handleNameChange}/>
							</div>
							<div class="checkout_info">
								<h3>대출 일시</h3>
								<CheckoutDate />
							</div>
							<div class="checkout_info">
								<h3>책 상태</h3>
								<input type="text" class="info_state" placeholder={CheckoutInfo.status} onChange={this.handleStatusChange}/>
							</div>
						</div>
						<div class="checkout_button">
							<form onSubmit={this.handleFormSubmit}>
								<input type="submit" class="btn" value="대출하기"/>
							</form>
						</div>
					</article>
				</Fragment>
			);
		}
		else
		{
			return (
				<div class="checkout-container">
					<div class="checkout-undefined">
						<h1>404 Not Found</h1>
						<h3>{CheckoutInfo.status}</h3>
						<Link to="/">
							<input type="button" class="btn" value="돌아가기"/>
						</Link>
					</div>
				</div>
			);
		}
	}
}

export default Checkout;
