import React, { Component } from 'react';
import './ItemInfo.css';
import './Button.js'
import { CheckOutButton, CheckInButton, CantCheckOutButton } from './Button.js';

// 저거 대출하기.. 비교해서 넣는거 해야됨. 
class ItemInfo extends Component{
	render () {
		let button;

		if (this.props.bookState === "borrow") {
			button = <CheckOutButton bookState = {this.props.bookState} />;
		} else if (this.props.bookState === "return"){
			button = <CheckInButton bookState = {this.props.bookState} />;
		} else {
			button = <CantCheckOutButton bookState = {this.props.bookState} />;
		}

		return (
			<div class="item">
				<img src={this.props.imgUrl} alt="책1"/>
				<div class="item-info">
					<h2>{this.props.bookName}</h2>
					<h3>{this.props.bookAuthor}</h3>
					<form action="#borrow" method="GET">
					</form>
				</div>
			</div>
		);
	}
}

export default ItemInfo;
